import * as React from 'react';
import './FormSidebar.css';
import IllustrationSecondary from '../../assets/images/ills2.png'

const x = [
    {
        title: 'Basic Information',
        children: ['Profile Photo', 'Design Title', 'Location', 'Profile Summary']
    },
    {
        title: 'Portfolio',
        children: ['Profile Photo', 'Design Title', 'Location', 'Profile Summary']
    },
    {
        title: 'Employee',
        children: ['Profile Photo', 'Design Title', 'Location', 'Profile Summary']
    }
]

const FormSidebar = () => {
    return (
        <div className={`sidebar secondary-sidebar`}>
            <div className="form-steps">
                <ul>
                    {
                        x.map((item, i) => (
                            <>
                                <p className="form-step-title mb-2 mt-4" key={i}><span className="item-number">{i + 1}</span>{item.title}</p>
                                <div className="form-details-wrapper">
                                    {
                                        item.children.map((item,i ) => {
                                            return <span className="form-step-detail" key={i}>{item}</span>
                                        })
                                    }
                                </div>
                            </>

                        ))
                    }
                </ul>
            </div>
            <div className="fixed-bottom">
                <img src={IllustrationSecondary} className="secondary-illustration" alt="illustration" width="200px"/>
            </div>
        </div>
    )
}

export default FormSidebar
