import * as React from 'react';
import './FormSidebar.css';
import IllustrationPrimary from '../../assets/images/ills1.png'

const x = [{
    BasicInformation: {
        title: 'Basic Information',
        children: ['Profile Photo', 'Design Title', 'Location', 'Profile Summary']
    }
}]

const FormSidebar = () => {
    return (
        <div className={`sidebar secondary-sidebar`}>
            <div className="form-steps">
                <ul>
                    {
                        x.map((item, i) => (
                            <>
                                <p className="form-step-title mb-2" key={i}><span className="item-number">{i + 1}</span>{item.BasicInformation.title}</p>
                                <div className="form-details-wrapper">
                                    {
                                        item.BasicInformation.children.map((item,i ) => {
                                            return <span className="form-step-detail" key={i}>{item}</span>
                                        })
                                    }
                                </div>
                            </>

                        ))
                    }
                </ul>
            </div>
            <div className="fixed-bottom">
                <img src={IllustrationPrimary} className="primary-illustration" alt="illustration" width="200px"/>
            </div>
        </div>
    )
}

export default FormSidebar
